import axios from 'axios';
import React ,{useEffect,useState}from 'react'

const ListEmployees = () => {
    const [Employees, setEmployees] = useState([]);
    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/user")
        .then(function(res) {
            setEmployees(res.data);
        })
        .catch(function(error) {
            console.log(error)
        });
      }, []);

  return (
  <div>
  <button className='btn btn-primary text-center '> Add Employee</button>

    <div className='row gap-3 justify-content-center'>

        {Employees&&Employees.map(emp=>{
            return<div className='col-12 col-md-3'>

            <div className="card" style={{width: "18rem"}}>
          <div classNameName="card-header">
            Employee
          </div>
          <ul className="list-group list-group-flush">
            <li className="list-group-item"> Name: {emp.username}</li>
            <li className="list-group-item">Email:  {emp.email}</li>
            <li className="list-group-item">Role: {emp.role}</li>
            <li className="list-group-item"><button className='btn btn-success' >Edit</button></li>
          </ul>
        </div>

            </div>
        })}

            </div>

  </div>
  )
}

export default ListEmployees
