import "./App.css";
import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import Container from "react-bootstrap/Container";
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import ListEmployees from "./pages/employees/ListEmployees";
import SignUp from "./pages/Registeration/SignUp";
import Login from "./pages/Registeration/Login";

function App() {
  const [currentUser, setCurrentUser] = useState(false);
  const [registrationToggle, setRegistrationToggle] = useState(false);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/auth/user")
      .then(function (res) {
        setCurrentUser(true);
      })
      .catch(function (error) {
        setCurrentUser(false);
      });
  }, []);

  function update_form_btn() {
    if (registrationToggle) {
      document.getElementById("form_btn").innerHTML = "Register";
      setRegistrationToggle(false);
    } else {
      document.getElementById("form_btn").innerHTML = "Log in";
      setRegistrationToggle(true);
    }
  }

  function submitLogout(e) {
    e.preventDefault();
    axios
      .post("http://127.0.0.1:8000/auth/logout/", {
        Headers: {
          Authorization: `Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNjg4Nzc4MDcwLCJpYXQiOjE2ODg3NzA4NzAsImp0aSI6ImNjMjBmZDYzNjE5YjQ4YTZhNGViOTg5OTM1YWRhZWE2IiwidXNlcl9pZCI6Nn0.6pbFAkRz3oZQrdiIfZNee1j8nWgp4te891hVQfKEWSM`,
        },
      })
      .then(function (res) {
        setCurrentUser(false);
      });
  }

  if (currentUser) {
    return (
      <div >
        <Navbar bg="dark" variant="dark">
          <Container>
            <Navbar.Brand>HR System</Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end">
              <Navbar.Text>
                <form onSubmit={e => submitLogout(e)}>
                  <Button type="submit" variant="light">Log out</Button>
                </form>
              </Navbar.Text>
            </Navbar.Collapse>
          </Container>
        </Navbar>
          <div className="center">
            <ListEmployees/>
          </div>
        </div>
    );
  }
  return (
    <div>
      <Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand>HR System</Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Navbar.Text>
              <Button id="form_btn" onClick={update_form_btn} variant="light">
                Register
              </Button>
            </Navbar.Text>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      {registrationToggle ? (
        <div className="center">
          <SignUp />
        </div>
      ) : (
        <div className="center">
          <Login />
        </div>
      )}
    </div>
  );
}

export default App;
