import axios from "axios";
import React, { useEffect, useState } from "react";

const ListEmployees = () => {
  const [Employees, setEmployees] = useState([]);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [contentError, setContentError] = useState("");
  const [newEmployee,setNewEmployee]=useState("")
  const [updateEmployee, setUpdateEmployee] = useState(false);


  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/user")
      .then(function (res) {
        setEmployees(res.data);
        setNewEmployee(false)
      })
      .catch(function (error) {
        console.log(error);
      });
  }, [newEmployee]);


// posting new 

async function handelSubmit(e) {
  e.preventDefault();
  if (!username.trim()) {
    setContentError("Add User Name for Employee!");
  }
  if (!email.trim()) {
    setContentError("Add Email for Employee!");
  }

  // proceed with form submission

  let form_data = new FormData();
  form_data.append("username", username);
  form_data.append("email", email);
  try {
    console.log(form_data);
    const response = await axios.post(
      "http://127.0.0.1:8000/api/user",
      form_data,
    );
    console.log(response.data);
    setEmail("");
    setUsername("");
    setNewEmployee(true)
  } catch (error) {
    console.error(error);
  }
}


  return (
    <div className="container justify-content-center">
      {/* Add employees */}
      <div className="mb-5">
        <form onSubmit={updateEmployee?handelSubmitForPostupdate:handelSubmit}>
          <div className="mb-3">
            <label htmlFor="Emailaddress" className="form-label">
              Email address
            </label>
            <input
              type="email"
              className="form-control"
              id="Emailaddress"
              aria-describedby="emailHelp"
              value={email}
              onChange={(e) => {
                setContentError("");
                setEmail(e.target.value);
              }}
            />
             {contentError && (
                            <p className="text-danger font-xssss">
                              {contentError}
                            </p>
                          )}
          </div>
          <div className="mb-3">
            <label htmlFor="Username" className="form-label">
              Username
            </label>
            <input type="text" className="form-control" id="Username" 
             value={username}
             onChange={(e) => {
               setContentError("");
               setUsername(e.target.value);
             }}
            />
             {contentError && (
                            <p className="text-danger font-xssss">
                              {contentError}
                            </p>
                          )}
          </div>

          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>

      <div className="justify-content-center w-100">
        {Employees &&
          Employees.map((emp) => {
            return (
              <div className=" w-75 mb-5">
                <div class="card">
                  <div class="card-header">{emp.role}</div>
                  <div class="card-body">
                    <h5 class="card-title"> Name: {emp.username}</h5>
                    <p class="card-text"> Email: {emp.email}</p>
                    <button onClick={()=>setUpdateEmployee(true)} class="btn btn-primary">
                      Edit Employee
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
      </div>
    </div>
  );
};

export default ListEmployees;
